legend( h, 'FW vs. n with errors', 'untitled fit 1', 'Location', 'NorthEast', 'Interpreter', 'none' );
% Label axes
xlabel( 'n', 'Interpreter', 'none' );
ylabel( 'FW', 'Interpreter', 'none' );
grid on


